# ai-context.rb - Project Context Configuration for xcontext CLI

# Project Identity
PROJ_NAME = "xcontext"

# Project Metadata
PROJECT_META = {
  type: 'cli',
  primary_language: 'rust',
  description: 'CLI tool to generate structured project context for AI models',
  dependencies: {
    cargo: 'Cargo.toml',
    ruby: ['Gemfile', 'Gemfile.lock']
  },
  runtime: { language_version: 'rust 2024' },
  build: {
    tools: ['cargo', 'rake'],
    commands: ['cargo build --release']
  },
  architecture: {
    entry_points: ['src/main.rs'],
  }
}

# Context Generation Settings
GENERATOR_CONFIG = {
  output_format: 'json',
  max_file_size: 512 * 1024,
  summarize_large_files: true,
  include_line_numbers: false
}

# Tree Generation Configuration
TREE_CONFIG = {
  ignore_patterns: [
    '.git/*',
    'target/*',
    '*.lock',
    '*.log',
    '.cache/*'
  ],
  options: {
    max_depth: 3,
    include_hidden: false,
    sort_by: 'name'
  }
}

# Files to Include
FILES_FOR_CONTEXT = {
  config_files: [
    'Cargo.toml',
  ],
  source_patterns: [
    'src/**/*.rs',
  ],
  specific_files: [
    'src/main.rs',
  ],
  docs: [
    'SPEC.org',
    'CLI.org',
    'README.org',
  ]
}

# MCP Server Configuration
MCP_CONFIG = {
  watch: true,
  tracked_files: FILES_FOR_CONTEXT,
  debounce_ms: 500
}

# Project-Specific Extensions
EXTENSIONS = {
  output_formats: ['json', 'yaml'],
}
