// --- src/main.rs ---
mod cli;
mod config;
mod context;
mod error;
mod output;
mod system_info;
mod utils;

use crate::cli::Cli;
use crate::config::{Config, DEFAULT_CONFIG_DIR, DEFAULT_CONFIG_FILENAME};
use crate::context::gather::{build_tree_from_paths, gather_files_and_tree, FileInfo};
use crate::context::ProjectContext;
use crate::error::AppError;

use clap::{CommandFactory, Parser};
use clap_complete::{generate, shells::Fish};
use clearscreen; // Added
use colored::*;
use notify_debouncer_mini::{new_debouncer, DebouncedEvent};
use std::collections::HashSet;
use std::fs;
use std::io::{Cursor};
use std::path::{Path, PathBuf};
use std::process;
use std::sync::{mpsc, Arc};


fn main() {
    let exit_code = match run_app() {
        Ok(_) => 0,
        Err(e) => {
            eprintln!("{} {}\n", "Error:".red().bold(), e); // Added newline for clarity
            e.exit_code()
        }
    };
    process::exit(exit_code);
}

// Removed is_terminating_utility_mode as clap handles conflicts better now

fn run_app() -> Result<(), AppError> {
    let cli_args = Cli::parse();

    // --- Handle -cl (Clear Screen) FIRST ---
    if cli_args.other_utils.clear_screen {
        clearscreen::clear().map_err(|e| AppError::UtilityMode {
            mode: "-cl".to_string(),
            details: format!("Failed to clear screen: {}", e),
        })?;
        return Ok(());
    }

    // --- Handle Completions ---
    if cli_args.completions_utils.completions_fish_show {
        let mut cmd = Cli::command();
        let mut buffer = Cursor::new(Vec::new());
        generate(Fish, &mut cmd, "xcontext", &mut buffer);
        let script = String::from_utf8(buffer.into_inner()).map_err(|e| {
            AppError::UtilityMode {
                mode: "--completions-fish-show".to_string(),
                details: format!("Failed to convert script to UTF-8: {}", e),
            }
        })?;
        println!("{}", script);
        return Ok(());
    }
    if cli_args.completions_utils.completions_fish_save {
        let mut cmd = Cli::command();
        let mut buffer = Cursor::new(Vec::new());
        generate(Fish, &mut cmd, "xcontext", &mut buffer);
        let script = String::from_utf8(buffer.into_inner()).map_err(|e| {
            AppError::UtilityMode {
                mode: "--completions-fish-save".to_string(),
                details: format!("Failed to convert script to UTF-8: {}", e),
            }
        })?;
        let completions_dir = dirs::config_dir()
            .ok_or_else(|| AppError::UtilityMode {
                mode: "--completions-fish-save".to_string(),
                details: "Could not determine config directory.".to_string(),
            })?
            .join("fish")
            .join("completions");
        fs::create_dir_all(&completions_dir)
            .map_err(|e| AppError::DirCreation { path: completions_dir.clone(), source: e })?;
        let completions_path = completions_dir.join("xcontext.fish");
        fs::write(&completions_path, script)
            .map_err(|e| AppError::FileWrite { path: completions_path.clone(), source: e })?;
        println!("{} Fish completions saved to: {}", "‚úÖ".green(), completions_path.display());
        return Ok(());
    }

    // --- Config Show/Save Needs Project Root first ---
     if cli_args.config_utils.config_show || cli_args.config_utils.config_save {
         // Determine project root using the updated logic (CLI -> Env Var -> CWD)
         let project_root = Config::determine_project_root(&cli_args)?;
         // Determine project name based on CLI or root fallback
         let determined_name = cli_args.config_project.project_name.clone()
             .unwrap_or_else(|| project_root.file_name().map(|n| n.to_string_lossy().to_string()).unwrap_or_else(|| "UnknownProject".to_string()));

         // Get default config and set the determined name & ensure default meta
         let mut config_to_show_or_save = Config::default();
         config_to_show_or_save.general.project_name = Some(determined_name);
         // MetaConfig::default() now handles enabling and adding author="json"

         if cli_args.config_utils.config_show {
             // Pass the prepared default config
             return utils::debug::print_config_toml(&config_to_show_or_save);
         }
         if cli_args.config_utils.config_save {
             let default_config_path = project_root.join(DEFAULT_CONFIG_DIR).join(DEFAULT_CONFIG_FILENAME);
             // Pass the prepared default config
             return utils::config_utils::save_config_to_path(&config_to_show_or_save, &default_config_path);
         }
     }


    // --- Other Show Utilities Need Merged Config ---
    if cli_args.show_utils.show_meta.is_some() || cli_args.show_utils.show_meta_all
       || cli_args.show_utils.show_prompt.is_some() || cli_args.show_utils.show_prompts
       || cli_args.show_utils.show_rule.is_some() || cli_args.show_utils.show_rules {
            // Determine root first
            let project_root_for_utils = Config::determine_project_root(&cli_args)?;
            // Load the merged config as these utilities depend on potentially overridden values
            let config_for_utils = Config::load_and_merge(&project_root_for_utils, &cli_args)?;

            if cli_args.show_utils.show_meta.is_some() || cli_args.show_utils.show_meta_all {
                return utils::show::handle_show_meta(&config_for_utils, cli_args.show_utils.show_meta, cli_args.show_utils.show_meta_all);
            }
            if cli_args.show_utils.show_prompt.is_some() || cli_args.show_utils.show_prompts {
                return utils::show::handle_show_prompt(cli_args.show_utils.show_prompt, cli_args.show_utils.show_prompts, &config_for_utils);
            }
            if cli_args.show_utils.show_rule.is_some() || cli_args.show_utils.show_rules {
                // Pass merged config for potential custom rules
                return utils::show::handle_show_rule(cli_args.show_utils.show_rule, cli_args.show_utils.show_rules, &config_for_utils);
            }
            // Should be unreachable due to clap conflicts, but good practice
            return Err(AppError::InvalidArg("Unhandled show utility mode state.".to_string()));
    }

    // --- Info Utilities Need Merged Config & Gathered Files ---
    // Debug and Metrics need to know *which* files would be included based on merged config.
    // We handle them inside generate_and_output_context after gathering files.
    // Determine root and load config *once* if we reach here (for Quick, Watch, or Generate).
    let project_root = Config::determine_project_root(&cli_args)?;
    let config = Arc::new(Config::load_and_merge(&project_root, &cli_args)?);

    // --- Quick Mode Needs Merged Config ---
    if let Some(pattern) = &cli_args.quick.quick {
        let quick_config = Arc::clone(&config);
        // Quick mode needs config for ignore settings
        return utils::quick::run_quick_mode(&project_root, pattern, &quick_config);
    }

    // --- Watch Mode Needs Merged Config ---
    if cli_args.watch.watch {
        let watch_config = Arc::clone(&config);
        let watch_project_root = project_root.clone();
        let watch_cli_args = cli_args.clone(); // Clone Cli args for watch loop
        return run_watch_mode(watch_project_root, watch_config, watch_cli_args);
    }

    // --- Default: Generate Context (also handles debug/metrics internally) ---
    generate_and_output_context(project_root, config, &cli_args)
}

// This function handles the main context generation, including debug/metrics checks
fn generate_and_output_context(
    project_root: PathBuf,
    config: Arc<Config>, // Takes merged config as Arc
    cli_args: &Cli,
) -> Result<(), AppError> {
    validate_args_for_generation(&config, cli_args)?;

    // Gather files based on the merged config (including soft ignores)
    let (source_files, docs_files, tree_path_types) =
        gather_files_and_tree(&project_root, &config)?;

    // --- Handle Debug/Metrics Modes Here (as they need gathered files) ---
    if cli_args.info_utils.debug {
        // Pass config reference for printing
        return utils::debug::print_debug_info(
            &config,
            &source_files,
            &docs_files,
            &tree_path_types,
            &project_root,
        );
    }
    if cli_args.info_utils.metrics {
        let combined_files: Vec<&FileInfo> = source_files.iter().chain(docs_files.iter()).collect();
        // Pass config reference for calculations
        let metrics =
            utils::metrics::calculate_metrics(&combined_files, &project_root, &config)?;
        return utils::metrics::print_metrics(&metrics);
    }

    // --- Proceed with full context generation ---
    let tree_for_context: Option<Vec<context::gather::TreeNode>> = if config.tree.enabled {
        Some(build_tree_from_paths(&tree_path_types)?)
    } else {
        None
    };

    // Build context using merged config reference
    let mut main_context = ProjectContext::build(
        &project_root,
        &config,
        tree_for_context,
    )?;

    if config.is_docs_section_active() {
        main_context = main_context.add_docs(docs_files, &project_root);
    }

    let mut output_target_path: Option<PathBuf> = None;
    let needs_saving_to_disk =
        cli_args.output_control.save.is_some() || cli_args.chunking.chunks.is_some();
    let force_stdout = cli_args.output_control.stdout;
    let default_to_stdout = !needs_saving_to_disk && !force_stdout;

    if config.source.enabled {
        if let Some(chunk_size_str) = &cli_args.chunking.chunks {
            let (save_dir, _, _) =
                config.get_save_details(cli_args.output_control.save.as_ref(), &project_root);
            // Pass Arc config to chunking as it might be long-running
            main_context = output::chunking::create_and_save_chunks(
                main_context,
                source_files,
                Arc::clone(&config), // Clone Arc for chunking
                cli_args.output_control.save.as_ref(),
                &project_root,
                chunk_size_str,
                &save_dir,
            )?;
        } else {
            main_context = main_context.add_files(source_files, &project_root);
        }
    }

    if needs_saving_to_disk {
        let (save_dir, filename_base, extension) =
            config.get_save_details(cli_args.output_control.save.as_ref(), &project_root);
        let is_chunked = cli_args.chunking.chunks.is_some();
        let should_save_main = if is_chunked {
            main_context
                .source
                .as_ref()
                .map_or(!config.source.enabled, |s| s.chunks.is_some())
        } else {
            true
        };
        if should_save_main || cli_args.output_control.save.is_some() {
            let main_filename = format!("{}.{}", filename_base, extension);
            output_target_path = Some(save_dir.join(main_filename));
        }
    }

    // Populate readme using merged config reference
    main_context.populate_ai_readme(&config);

    if force_stdout || default_to_stdout {
        // Pass config reference for output serialization settings
        output::output_context(&main_context, &config, None)?;
    } else if let Some(path) = output_target_path {
        // Pass config reference for output serialization settings
        output::output_context(&main_context, &config, Some(&path))?;
    }
    Ok(())
}

fn validate_args_for_generation(config: &Config, cli_args: &Cli) -> Result<(), AppError> {
    if cli_args.chunking.chunks.is_some() {
        if !config.source.enabled {
            return Err(AppError::InvalidArg(
                "--chunks cannot be used when source file inclusion is disabled".to_string(),
            ));
        }
        if config.output.format != "json" {
            return Err(AppError::Chunking(
                "Chunking requires the output format to be 'json'".to_string(),
            ));
        }
        if cli_args.output_control.stdout {
            return Err(AppError::InvalidArg(
                "--stdout cannot be used with --chunks".to_string(),
            ));
        }
    }
    Ok(())
}


fn run_watch_mode(
    project_root: PathBuf,
    mut config: Arc<Config>, // Starts with initial merged config
    cli_args: Cli, // Cloned CLI args
) -> Result<(), AppError> {
    println!(
        "üëÄ Starting watch mode for '{}'. Press Ctrl+C to exit.",
        project_root.display()
    );

    // Helper to watch a single path
    fn watch_path(
        watcher: &mut dyn notify::Watcher,
        path: &Path,
        watched_paths: &mut HashSet<PathBuf>,
    ) -> Result<(), AppError> {
        // Use canonicalize to handle symlinks consistently if possible
        let path_to_watch = path.canonicalize().unwrap_or_else(|_| path.to_path_buf());
        if !watched_paths.contains(&path_to_watch) {
            watcher
                .watch(&path_to_watch, notify::RecursiveMode::NonRecursive)
                .map_err(|e| {
                    AppError::WatchError(format!("Failed to watch {}: {}", path.display(), e))
                })?;
            watched_paths.insert(path_to_watch);
        }
        Ok(())
    }

    // --- Initial Generation ---
    let initial_config = Arc::clone(&config);
    let initial_project_root = project_root.clone();
    // Call generate_and_output_context for initial run
    // Pass initial_project_root here
    if let Err(e) = generate_and_output_context(initial_project_root.clone(), initial_config, &cli_args) {
        eprintln!("{} {}", "‚ö†Ô∏è Error during initial generation:".yellow(), e);
    } else {
        println!("{}", "‚úÖ Initial generation complete.".green());
    }

    // --- Setup Watcher ---
    let (debouncer_tx, main_rx) = mpsc::channel::<Result<Vec<DebouncedEvent>, notify::Error>>();
    let delay = config.get_watch_delay()?;
    let mut debouncer = new_debouncer(delay, debouncer_tx)
        .map_err(|e| AppError::WatchError(format!("Failed to create debouncer: {}", e)))?;
    let mut watched_paths = HashSet::new(); // Track currently watched paths

    // Helper to setup/reset watches based on current config
    let setup_watches =
        |current_config: &Arc<Config>,
         watcher: &mut dyn notify::Watcher,
         current_watched: &mut HashSet<PathBuf>|
         -> Result<(), AppError> {
        // Unwatch previous paths
        for path in current_watched.iter() {
            let _ = watcher.unwatch(path); // Ignore errors if already unwatched
        }
        current_watched.clear();

        // Gather files based on *current* config to determine what to watch
        match gather_files_and_tree(&project_root, current_config) {
            Ok((source_files, docs_files, _)) => {
                if current_config.source.enabled {
                    for file_info in source_files {
                        let _ = watch_path(watcher, &file_info.path, current_watched);
                    }
                }
                if current_config.is_docs_section_active() {
                    for file_info in docs_files {
                        let _ = watch_path(watcher, &file_info.path, current_watched);
                    }
                }
            }
            Err(e) => {
                // Log error but continue, maybe files will appear later
                eprintln!("{} {}", "‚ö†Ô∏è Error gathering files for watch mode:".yellow(), e);
            }
        }

        // Watch the config file itself
        let resolved_config_path = Config::resolve_config_path(
            &project_root,
            cli_args.config_project.context_file.as_ref(),
            cli_args.config_project.disable_context_file,
        )?;
        if let Some(ref config_path_to_watch) = resolved_config_path {
            if config_path_to_watch.exists() { // Only watch if it exists
                 match watch_path(watcher, config_path_to_watch, current_watched) {
                    Ok(_) => {},
                    Err(e) => eprintln!("{} {}: {}", "‚ö†Ô∏è Error watching config file".yellow(), config_path_to_watch.display(), e),
                 }
            }
        }

        if current_watched.is_empty() {
            println!("{}", "‚ö†Ô∏è No source, documentation, or config files found to watch based on current configuration.".yellow());
        } else {
            println!("üîç Watching {} files/paths...", current_watched.len());
        }
        Ok(())
    };

    // Setup initial watches
    if let Err(e) = setup_watches(&config, debouncer.watcher(), &mut watched_paths) {
        eprintln!("{} {}", "‚ö†Ô∏è Error setting up initial watches:".yellow(), e);
        // Decide whether to exit or try to continue
        // return Err(e); // Or just log and proceed
    }


    // --- Watch Loop ---
    loop {
        match main_rx.recv() {
            Ok(event_result) => match event_result {
                Ok(debounced_events) => {
                    if !debounced_events.is_empty() {
                        // Check if the config file was among the changes
                        let resolved_config_path = Config::resolve_config_path(
                            &project_root,
                            cli_args.config_project.context_file.as_ref(),
                            cli_args.config_project.disable_context_file,
                        )?;
                        let config_changed = if let Some(ref current_config_path) = resolved_config_path {
                            let canonical_config_path = current_config_path.canonicalize().ok();
                            debounced_events.iter().any(|event| {
                                // Compare canonical paths if possible
                                let event_path_canonical = event.path.canonicalize().ok();
                                if let (Some(conf_canon), Some(evt_canon)) = (&canonical_config_path, &event_path_canonical) {
                                    conf_canon == evt_canon
                                } else {
                                    // Fallback to direct comparison
                                    event.path == *current_config_path
                                }
                            })
                        } else { false }; // No config file path resolved, so it couldn't have changed

                        // Reload config and reset watches if config changed
                        if config_changed {
                            println!( "\n{}", "üîÑ Detected change in config file. Reloading configuration...".blue() );
                            match Config::load_and_merge(&project_root, &cli_args) {
                                Ok(reloaded_config) => {
                                    config = Arc::new(reloaded_config); // Update the shared config
                                    println!("{}", "‚úÖ Configuration reloaded.".green());
                                    println!("{}", "üîÑ Rewatching files based on new configuration...".blue());
                                    // Reset watches based on the *new* config
                                    if let Err(e) = setup_watches(&config, debouncer.watcher(), &mut watched_paths) {
                                        eprintln!("{} {}", "‚ö†Ô∏è Error setting up watches after config reload:".yellow(), e);
                                    }
                                }
                                Err(e) => {
                                    eprintln!("{} {}", "‚ö†Ô∏è Error reloading config:".yellow(), e);
                                    // Keep using the old config if reload fails
                                }
                            }
                        }

                        // Regenerate context regardless of whether config changed (source file might have changed)
                        println!( "\n{} {} event(s). Regenerating context...", "üîÑ Detected file change in".blue(), debounced_events.len() );
                        let current_config = Arc::clone(&config); // Clone Arc for generation task
                        let current_project_root = project_root.clone(); // Clone project_root for this generation
                        // *** FIX: Add missing project_root argument ***
                        if let Err(e) = generate_and_output_context(current_project_root, current_config, &cli_args) {
                            eprintln!("{} {}", "‚ö†Ô∏è Error during regeneration:".yellow(), e);
                        } else {
                            println!("{}", "‚úÖ Regeneration complete.".green());
                        }
                    }
                }
                Err(error) => {
                    // Log notify errors
                    eprintln!("{} {}", "‚ö†Ô∏è Watch error:".yellow(), error);
                }
            },
            Err(e) => {
                // Channel disconnected, exit loop
                eprintln!("{} {}", "‚õî Watcher channel error:".red(), e);
                break;
            }
        }
    }
    Ok(())
}
