[package]
name = "xcontext-cli"
version = "0.1.0"
edition = "2024"                           # Assuming 2024 edition, adjust if needed
description = "CLI interface for xcontext"
license = "GPL-3.0-or-later"
publish = false

[[bin]]
name = "xcontext"
path = "cli.rs"

[dependencies]
# Workspace dependencies
anyhow = { workspace = true }
log = { workspace = true }
clap = { workspace = true }
colored = { workspace = true }
env_logger = { workspace = true }
toml = { workspace = true }       # Added
pathdiff = { workspace = true }   # Added
rayon = { workspace = true }      # Added
ignore = { workspace = true }     # Added

# Local Core Crate
xcontext-core = { path = "../core" }

# CLI specific dependencies (from workspace or direct)
clap_complete = { workspace = true }
clearscreen = { workspace = true }
comfy-table = { workspace = true }
dirs = { workspace = true }
notify = { workspace = true }
notify-debouncer-mini = { workspace = true }
serde = { workspace = true }
serde_json = { workspace = true }
serde_yml = { workspace = true }
quick-xml = { workspace = true }
tiktoken-rs = { workspace = true }
byte-unit = { workspace = true }

# Direct dependencies (if not in workspace)
glob = "0.3" # Added: needed for quick command (specify version)

mod cli_args;
mod commands;
mod output;
mod watch;

use anyhow::{Context, Result};
use clap::{CommandFactory, Parser};
use colored::*;
use log;
use std::process;
// Removed unused Arc import

// Corrected import: Added GenerateArgs
use cli_args::{Cli, Commands, FormatOutputOpts, GenerateArgs, ProjectConfigOpts};
use xcontext_core::{AppError, Config}; // Use Config from core crate

fn main() {
    let cli_args = Cli::parse();

    setup_logging(cli_args.quiet, cli_args.verbose);

    let quiet = cli_args.quiet;
    let verbose = cli_args.verbose;

    log::debug!("CLI args parsed: {:?}", cli_args);

    let exit_code = match run_app(cli_args, quiet, verbose) {
        Ok(_) => {
            log::info!("Application finished successfully.");
            0
        }
        Err(e) => {
            let core_err = e.downcast_ref::<xcontext_core::AppError>();
            let exit_code = match core_err {
                // Define exit codes based on core errors if needed
                Some(AppError::Config(_)) => 1,
                Some(AppError::TomlParse(_)) => 1,
                Some(AppError::TomlSerialize(_)) => 1,
                Some(AppError::Io(_)) => 2,
                Some(AppError::FileRead { .. }) => 2,
                Some(AppError::FileWrite { .. }) => 2,
                Some(AppError::DirCreation { .. }) => 2,
                Some(AppError::WalkDir(_)) => 2,
                Some(AppError::Ignore(_)) => 2,
                Some(AppError::RuleLoading(_)) => 2,
                Some(AppError::Glob(_)) => 2,
                Some(AppError::Chunking(_)) => 3,
                Some(AppError::SystemInfo(_)) => 4,
                Some(AppError::InvalidArgument(_)) => 5,
                // Add mapping for AppError::ClapUsage if you move clap errors to core
                Some(AppError::JsonSerialize(_)) => 6,
                Some(AppError::YamlError(_)) => 6,
                Some(AppError::XmlSerialize(_)) => 6,
                Some(AppError::TikToken(_)) => 8,
                // Add mapping for AppError::WatchError if moved back to core
                Some(AppError::DataLoading(_)) => 1, // Treat data loading like config error
                Some(AppError::DurationParse(_)) => 5, // Treat like invalid arg
                // Corrected: Added wildcard arm for non-exhaustive AppError
                Some(_) => 1, // Default exit code for other *core* AppErrors
                None => 1,    // Default exit code for other *anyhow* errors
            };

            // Only print error if not quiet, or if it's a critical config/clap error
            // This prevents noisy error messages for things handled by core logging
            if !quiet || exit_code == 1 || exit_code == 5 {
                eprintln!("{} {:#}\\n", "Error:".red().bold(), e);
            } else {
                // Log even if quiet for critical errors
                log::error!("Application failed: {:#}", e);
            }

            exit_code
        }
    };
    log::debug!("Exiting with code {}", exit_code);
    process::exit(exit_code);
}

fn setup_logging(quiet: bool, verbose: u8) {
    let log_level = if quiet {
        log::LevelFilter::Off // Turn off logging completely if quiet
    } else {
        match verbose {
            0 => log::LevelFilter::Warn,  // Default: Show warnings and errors
            1 => log::LevelFilter::Info,  // -v: Show info, warnings, errors
            2 => log::LevelFilter::Debug, // -vv: Show debug, info, warnings, errors
            _ => log::LevelFilter::Trace, // -vvv+: Show all levels
        }
    };
    env_logger::Builder::new()
        .filter_level(log_level)
        .format_timestamp(None) // Keep logs clean
        .init();
    log::trace!("Logger initialized with level: {:?}", log_level);
}

fn run_app(cli: Cli, quiet: bool, verbose: u8) -> Result<()> {
    match cli.command {
        None => {
            Cli::command().print_help()?;
        }
        Some(command) => {
            match command {
                Commands::Cl => {
                    log::debug!("Executing 'cl' command...");
                    clearscreen::clear().context("Failed to clear screen")?;
                    log::debug!("Screen cleared.");
                }
                Commands::Completion(args) => {
                    log::debug!("Executing 'completion' command...");
                    commands::completion::handle_completion_command(&args, quiet)?;
                }
                Commands::Config(args) => {
                    log::debug!("Executing 'config' command...");
                    let temp_opts = ProjectConfigOpts::default();
                    let project_root =
                        Config::determine_project_root(temp_opts.project_root.as_ref())
                            .context("Failed to determine project root for config command")?;
                    commands::config::handle_config_command(&args, &project_root, quiet)?;
                }
                Commands::Mcp(_args) => {
                    log::warn!("Executing dummy 'mcp' command...");
                    eprintln!("MCP command not implemented yet.");
                }
                Commands::Generate(args) => {
                    log::debug!("Executing 'generate' command...");
                    commands::generate::handle_generate_command(args, quiet, verbose)?;
                }
                Commands::Watch(args) => {
                    log::debug!("Executing 'watch' command...");
                    // run_watch_mode now takes args directly
                    watch::run_watch_mode(args, quiet, verbose)?;
                }
                Commands::Show(args) => {
                    log::debug!("Executing 'show' command...");
                    commands::show::handle_show_command(args, quiet, verbose)?;
                }
                Commands::Metrics(args) => {
                    log::debug!("Executing 'metrics' command...");
                    commands::metrics::handle_metrics_command(args, quiet)?;
                }
                Commands::Debug(args) => {
                    log::debug!("Executing 'debug' command...");
                    commands::debug::handle_debug_command(args, quiet, verbose)?;
                }
                Commands::Quick(args) => {
                    log::debug!("Executing 'quick' command...");
                    commands::quick::handle_quick_command(args, quiet, verbose)?;
                }
            }
        }
    }
    Ok(())
}

// Kept this function as it seems used by load_config_for_command
fn merge_config_with_cli_overrides(mut config: Config, args: &GenerateArgs) -> Config {
    log::trace!("Applying generate command CLI overrides to config...");

    if let Some(name) = &args.project_config.project_name {
        config.general.project_name = Some(name.clone());
    }

    // Output Format Overrides
    if let Some(format) = &args.format_output.format {
        config.output.format = format.clone();
    }
    // Apply JSON minify logic based on flags and format
    config.output.json_minify = if config.output.format == "json" {
        !args.format_output.disable_json_minify // default to true (minify) unless disable flag is set
    } else {
        true // Default irrelevant for non-JSON, keep consistent default
    };
    // Apply XML pretty print logic based on flags and format
    config.output.xml_pretty_print = if config.output.format == "xml" {
        args.format_output.enable_xml_pretty // default to false (compact) unless enable flag is set
    } else {
        false // Default irrelevant for non-XML
    };

    // Exclusion Overrides
    if args.exclusion.exclude_project_name {
        config.output.include_project_name = false;
    }
    if args.exclusion.exclude_project_root {
        config.output.include_project_root = false;
    }
    if args.exclusion.exclude_timestamp {
        config.output.include_timestamp = false;
    }
    if args.exclusion.exclude_system_info {
        config.output.include_system_info = false;
    }

    // Section Toggle Overrides
    if args.section_toggles.disable_tree {
        config.tree.enabled = false;
    }
    if args.section_toggles.enable_tree {
        config.tree.enabled = true;
    }
    if args.section_toggles.disable_source {
        config.source.enabled = false;
    }
    if args.section_toggles.enable_source {
        config.source.enabled = true;
    }
    if args.section_toggles.disable_meta {
        config.meta.enabled = false;
    }
    if args.section_toggles.enable_meta {
        config.meta.enabled = true;
    }
    if args.section_toggles.disable_rules {
        config.rules.enabled = false;
    }
    if args.section_toggles.enable_rules {
        config.rules.enabled = true;
    }
    if args.section_toggles.disable_docs {
        config.docs.enabled = false;
    }
    if args.section_toggles.enable_docs {
        config.docs.enabled = true;
    }

    // Ignore Toggle Overrides
    if args.ignore_toggles.disable_gitignore {
        config.general.use_gitignore = false;
    }
    if args.ignore_toggles.enable_gitignore {
        config.general.use_gitignore = true;
    }
    if args.ignore_toggles.disable_builtin_ignore {
        config.general.enable_builtin_ignore = false;
    }
    if args.ignore_toggles.enable_builtin_ignore {
        config.general.enable_builtin_ignore = true;
    }

    // Filter Overrides
    if !args.filters.tree_include.is_empty() {
        config.tree.include = Some(args.filters.tree_include.clone());
    }
    if !args.filters.tree_exclude.is_empty() {
        config.tree.exclude = Some(args.filters.tree_exclude.clone());
    }
    if !args.filters.source_include.is_empty() {
        config.source.include = Some(args.filters.source_include.clone());
    }
    if !args.filters.source_exclude.is_empty() {
        config.source.exclude = Some(args.filters.source_exclude.clone());
    }
    if !args.filters.docs_include.is_empty() {
        config.docs.include = Some(args.filters.docs_include.clone());
    }
    if !args.filters.docs_exclude.is_empty() {
        config.docs.exclude = Some(args.filters.docs_exclude.clone());
    }

    // Meta Override
    if !args.meta_override.add_meta.is_empty() {
        log::trace!("Applying meta overrides: {:?}", args.meta_override.add_meta);
        config.meta.enabled = true; // Ensure meta section is enabled if adding via CLI
        for (key, value) in &args.meta_override.add_meta {
            config.meta.custom_meta.insert(key.clone(), value.clone());
        }
    }

    // Watch specific args are handled separately if needed (e.g., in watch command)
    log::trace!("Config after CLI overrides: {:?}", config);
    config
}

// Helper function to load config considering CLI options
// Kept public as it's used by multiple command modules
pub fn load_config_for_command(
    project_root: &std::path::Path,
    project_opts: &ProjectConfigOpts,
    // Pass specific args structs for commands that can override config parts
    generate_args: Option<&cli_args::GenerateArgs>,
    watch_args: Option<&cli_args::WatchArgs>,
    format_override: Option<&FormatOutputOpts>, // For commands like show, metrics, debug, quick
) -> Result<Config> {
    let config_path = Config::resolve_config_path(
        project_root,
        project_opts.context_file.as_ref(),
        project_opts.disable_context_file,
    )
    .context("Failed to resolve configuration path")?;

    let mut config = match &config_path {
        Some(path) => Config::load_from_path(path)
            .with_context(|| format!("Failed to load config from {}", path.display()))?,
        None => Config::default(),
    };

    // Apply overrides from GenerateArgs if provided
    if let Some(gen_args) = generate_args {
        config = merge_config_with_cli_overrides(config, gen_args);
    } else {
        // Apply overrides common to other commands if needed
        if let Some(name) = &project_opts.project_name {
            config.general.project_name = Some(name.clone());
        }
        // Apply format overrides if present
        if let Some(fmt_opts) = format_override {
            if let Some(format) = &fmt_opts.format {
                config.output.format = format.clone();
            }
            config.output.json_minify = if config.output.format == "json" {
                !fmt_opts.disable_json_minify
            } else {
                true
            };
            config.output.xml_pretty_print = if config.output.format == "xml" {
                fmt_opts.enable_xml_pretty
            } else {
                false
            };
        }
        // Apply watch-specific overrides if present
        if let Some(w_args) = watch_args {
            if let Some(delay) = &w_args.watch_delay {
                config.watch.delay = delay.clone();
            }
            // Note: watch also uses format_override logic handled above if needed
        }
    }

    // Ensure project name is set (fallback to directory name)
    config.general.project_name = Some(config.get_effective_project_name(project_root));

    Ok(config)
}

use anyhow::{Context, Result};
use clap::CommandFactory;
use clap_complete::{Shell, generate};
use colored::*;
use std::fs::{self, File};
use std::io::{self, Write};
use xcontext_core::AppError; // Use core error for specific cases if needed

use crate::cli_args::{Cli, CompletionArgs};

pub fn handle_completion_command(args: &CompletionArgs, quiet: bool) -> Result<()> {
    let shell_str = args.shell.as_deref().unwrap_or("fish");
    let save_output = args.save;

    let shell_enum: Shell = match shell_str.to_lowercase().as_str() {
        "fish" => Shell::Fish,
        "bash" => Shell::Bash,
        "zsh" => Shell::Zsh,
        _ => {
            anyhow::bail!(AppError::InvalidArgument(format!(
                // Use anyhow::bail! for CLI errors
                "Unsupported shell for completion: {}",
                shell_str
            )));
        }
    };

    let mut command = Cli::command();
    let bin_name = command.get_name().to_string();

    if !save_output {
        generate(shell_enum, &mut command, bin_name, &mut io::stdout());
    } else {
        let save_dir_res = match shell_enum {
            Shell::Fish => dirs::config_dir().map(|p| p.join("fish").join("completions")),
            Shell::Bash => dirs::config_dir().map(|p| p.join("bash_completion.d")), // Common location
            Shell::Zsh => dirs::data_local_dir().map(|p| p.join("zsh").join("site-functions")),
            _ => anyhow::bail!(AppError::InvalidArgument(format!(
                "Default save location not known for shell: {}",
                shell_str
            ))),
        };

        let save_dir = save_dir_res
            .ok_or_else(|| anyhow::anyhow!("Could not determine standard completion directory."))?;

        let filename = match shell_enum {
            Shell::Fish => format!("{}.fish", bin_name),
            Shell::Bash => format!("{}.bash", bin_name), // Or just bin_name
            Shell::Zsh => format!("_{}", bin_name),
            _ => unreachable!(),
        };
        let save_path = save_dir.join(&filename); // Use reference

        if save_path.exists() {
            if !quiet {
                print!(
                    "{} Completion file already exists at '{}'. Overwrite? [{}/{}] ",
                    "âš ï¸".yellow(),
                    save_path.display().to_string().cyan(),
                    "y".green(),
                    "N".red()
                );
                io::stdout().flush().context("Failed to flush stdout")?;
                let mut response = String::new();
                io::stdin()
                    .read_line(&mut response)
                    .context("Failed to read user input")?;
                if !response.trim().eq_ignore_ascii_case("y") {
                    println!("Save cancelled.");
                    return Ok(());
                }
            } else {
                anyhow::bail!(
                    "Target file '{}' exists. Overwrite prevented in quiet mode.",
                    save_path.display()
                );
            }
        }

        fs::create_dir_all(&save_dir)
            .with_context(|| format!("Failed to create directory {}", save_dir.display()))?;
        let mut file = File::create(&save_path)
            .with_context(|| format!("Failed to create file {}", save_path.display()))?;
        generate(shell_enum, &mut command, bin_name, &mut file);

        if !quiet {
            println!(
                "{} {} completions saved to: {}",
                "âœ…".green(),
                shell_str.cyan(),
                save_path.display().to_string().blue()
            );
        }
    }
    Ok(())
}

use crate::cli_args::ConfigArgs;
use crate::output::print_data_or_text; // Use unified output helper
use anyhow::{Context, Result};
use colored::*;
use std::fs;
use std::io::{self, Write};
use std::path::Path;
use toml; // Added use
use xcontext_core::{
    Config,
    config::{DEFAULT_CONFIG_DIR, DEFAULT_CONFIG_FILENAME}, // Use core constants
};

pub fn handle_config_command(args: &ConfigArgs, project_root: &Path, quiet: bool) -> Result<()> {
    let determined_name = project_root
        .file_name()
        .map(|n| n.to_string_lossy().to_string())
        .unwrap_or_else(|| "UnknownProject".to_string());

    let mut config_to_show_or_save = Config::default();
    config_to_show_or_save.general.project_name = Some(determined_name);

    if args.save {
        let default_config_path = project_root
            .join(DEFAULT_CONFIG_DIR)
            .join(DEFAULT_CONFIG_FILENAME);
        save_config_to_path(&config_to_show_or_save, &default_config_path, quiet)
    } else {
        // Serialize default config to TOML string for printing
        let toml_string = toml::to_string_pretty(&config_to_show_or_save) // Added use toml
            .context("Failed to serialize default config to TOML")?;
        // Use print_data_or_text (though it defaults to JSON/YAML/XML, text fallback works)
        print_data_or_text(
            &config_to_show_or_save,                       // Pass data even if using text
            Some(toml_string),                             // Provide the TOML string as plain text
            &crate::cli_args::FormatOutputOpts::default(), // Use default format options
            "text",                                        // Default to text for this command
            "Config",                                      // XML root name if XML were used
        )
    }
}

fn save_config_to_path(config: &Config, path: &Path, quiet: bool) -> Result<()> {
    if path.exists() {
        if quiet {
            anyhow::bail!(
                "Target file '{}' exists. Overwrite prevented in quiet mode.",
                path.display()
            );
        }
        print!(
            "{} Config file exists at '{}'. Overwrite? [{}/{}] ",
            "âš ï¸".yellow(),
            path.display().to_string().cyan(),
            "y".green(),
            "N".red()
        );
        io::stdout().flush().context("Failed to flush stdout")?;
        let mut response = String::new();
        io::stdin()
            .read_line(&mut response)
            .context("Failed to read user input")?;
        if !response.trim().eq_ignore_ascii_case("y") {
            println!("Save cancelled.");
            return Ok(());
        }
    }

    if let Some(parent) = path.parent() {
        fs::create_dir_all(parent)
            .with_context(|| format!("Failed to create directory {}", parent.display()))?;
    }

    let header = format!(
        "{}\\n{}\\n\\n", // Add extra newline
        "# Default xcontext configuration", "# Generated by 'xcontext config --save'"
    );
    let toml_string =
        toml::to_string_pretty(config).context("Failed to serialize default config to TOML")?; // Added use toml
    let final_content = format!("{}{}", header, toml_string);

    fs::write(path, final_content)
        .with_context(|| format!("Failed to write config file {}", path.display()))?;

    if !quiet {
        println!(
            "{} Configuration saved successfully to: {}",
            "âœ…".green(),
            path.display().to_string().blue()
        );
    }
    Ok(())
}

use crate::cli_args::DebugArgs; // Removed unused FormatOutputOpts
use crate::load_config_for_command;
use crate::output::print_data_or_text;
use anyhow::{Context, Result};
use colored::*;
use log;
use pathdiff; // Added use
use serde::Serialize;
use std::collections::BTreeMap;
use std::path::Path;
use toml; // Added use
use xcontext_core::{self as core, Config, FileInfo, ResolvedRules}; // Removed unused 'config' import alias

#[derive(Debug, Serialize)]
struct DebugInfo<'a> {
    effective_config: &'a Config,
    source_files_to_include: Vec<String>,
    docs_files_to_include: Vec<String>,
    tree_elements_to_include: &'a [(String, bool)], // path, is_dir
    resolved_rules: &'a ResolvedRules,
}

pub fn handle_debug_command(args: DebugArgs, quiet: bool, _verbose: u8) -> Result<()> {
    // Marked verbose as unused
    let project_root = Config::determine_project_root(args.project_config.project_root.as_ref())
        .context("Failed to determine project root")?;
    log::info!("Project root determined: {}", project_root.display());

    let config = load_config_for_command(
        &project_root,
        &args.project_config,
        None,
        None,
        Some(&args.format_output), // Pass format override options
    )
    .context("Failed to load configuration for debug command")?;

    log::debug!("Debug: Gathering file lists...");
    let (source_files, docs_files, tree_path_types) =
        core::gather_files_and_tree(&project_root, &config, quiet)
            .context("Failed to gather file lists for debug")?;
    log::debug!("Debug: File lists gathered.");

    log::debug!("Debug: Detecting project characteristics...");
    let project_characteristics = core::detect_project_characteristics(&project_root)
        .context("Failed to detect project characteristics for debug")?;
    log::debug!("Debug: Characteristics detected.");

    log::debug!("Debug: Resolving rules...");
    let resolved_rules =
        core::config::resolve_rules(&config.rules, &project_root, &project_characteristics)
            .context("Failed to resolve rules for debug")?;
    log::debug!("Debug: Rules resolved.");

    let debug_data = DebugInfo {
        effective_config: &config,
        source_files_to_include: get_relative_paths(&source_files, &project_root),
        docs_files_to_include: get_relative_paths(&docs_files, &project_root),
        tree_elements_to_include: &tree_path_types,
        resolved_rules: &resolved_rules,
    };

    if args.format_output.format.is_none() {
        log::debug!("Debug: Printing pretty output...");
        print_debug_info_pretty(&debug_data, &project_root)?;
        log::debug!("Debug: Pretty output complete.");
    } else {
        log::debug!(
            "Debug: Printing structured output (format: {:?})...",
            args.format_output.format
        );
        // Pass None for plain_text, rely on structured output
        print_data_or_text(&debug_data, None, &args.format_output, "json", "DebugInfo")?;
        log::debug!("Debug: Structured output complete.");
    }
    Ok(())
}

fn get_relative_paths(files: &[FileInfo], project_root: &Path) -> Vec<String> {
    files
        .iter()
        .map(|f| {
            pathdiff::diff_paths(&f.path, project_root) // Added use pathdiff
                .map(|p| p.to_string_lossy().to_string())
                .unwrap_or_else(|| f.path.to_string_lossy().to_string()) // Fallback
        })
        .collect()
}

fn print_debug_info_pretty(debug_info: &DebugInfo, _project_root: &Path) -> Result<()> {
    println!(
        "{}",
        "\n--- Effective Configuration ---"
            .green()
            .bold()
            .underline()
    );
    let config_toml = toml::to_string_pretty(debug_info.effective_config) // Added use toml
        .context("Failed to serialize effective config to TOML")?;
    println!("{}", config_toml);

    print_path_list("Source Files Included", &debug_info.source_files_to_include);
    print_path_list("Docs Files Included", &debug_info.docs_files_to_include);

    println!(
        "{}",
        "\n--- Tree Elements Included ---"
            .green()
            .bold()
            .underline()
    );
    if debug_info.tree_elements_to_include.is_empty() {
        println!("{}", "(None)".dimmed());
    } else {
        // Sort tree elements for consistent output
        let mut sorted_tree = debug_info.tree_elements_to_include.to_vec();
        sorted_tree.sort();
        for (p, is_dir) in sorted_tree {
            let suffix = if is_dir {
                " (dir)".dimmed()
            } else {
                "".normal()
            };
            println!("- {}{}", p.cyan(), suffix);
        }
    }

    display_debug_rules(debug_info.resolved_rules);

    println!("{}", "\n--- End Debug Info ---".green().bold());
    Ok(())
}

fn print_path_list(title: &str, paths: &[String]) {
    println!(
        "{}",
        format!("\n--- {} ---", title).green().bold().underline()
    );
    if paths.is_empty() {
        println!("{}", "(None)".dimmed());
    } else {
        // Assume paths are already sorted from gather step if needed, or sort here
        let mut sorted_paths = paths.to_vec();
        sorted_paths.sort();
        sorted_paths.iter().for_each(|p| println!("- {}", p.cyan()));
    }
}

fn display_debug_rules(resolved_rules: &ResolvedRules) {
    println!("{}", "\n--- Resolved Rules ---".green().bold().underline());
    if resolved_rules.rulesets.is_empty() {
        println!("{}", "(No rules enabled or resolved)".dimmed());
        return;
    }
    println!(
        "{:<35} {:<18} {:<10}",
        "Ruleset Key".bold(),
        "Origin".bold(),
        "Rule Count".bold()
    );
    println!("{:-<65}", ""); // Separator line

    // Sort rules by key for consistent output
    let sorted_rules: BTreeMap<_, _> = resolved_rules.rulesets.iter().collect();

    for (key, rules_list) in sorted_rules {
        let origin_str = resolved_rules
            .origins
            .get(key)
            .map_or("unknown", |s| s.as_str());
        let origin_colored = match origin_str {
            "default" | "default+include" => origin_str.cyan(),
            "dynamic" => origin_str.magenta(),
            "include" => origin_str.green(),
            "import" => origin_str.yellow(),
            "custom" => origin_str.blue(),
            _ => origin_str.dimmed(),
        };
        println!(
            "{:<35} {:<24} {:<10}", // Adjust spacing if needed
            key.blue(),
            origin_colored,
            rules_list.len()
        );
    }
}

use crate::cli_args::GenerateArgs; // Removed unused WatchArgs
use crate::load_config_for_command;
use crate::output;
use anyhow::{Context, Result};
use colored::Colorize;
use log;
use std::fs; // Added use std::fs
use std::path::{Path, PathBuf};
use std::sync::Arc;
use xcontext_core::{self as core, Config, ProjectContext}; // Use core types

pub fn handle_generate_command(args: GenerateArgs, quiet: bool, verbose: u8) -> Result<()> {
    let project_root = Config::determine_project_root(args.project_config.project_root.as_ref())
        .context("Failed to determine project root")?;
    log::info!("Project root determined: {}", project_root.display());

    let config = Arc::new(
        load_config_for_command(
            &project_root,
            &args.project_config,
            Some(&args), // Pass generate args for overrides
            None,
            None, // Format handled within load_config_for_command via generate_args
        )
        .context("Failed to load configuration")?,
    );

    // Create OutputTargetArgs from GenerateArgs
    let output_target_args = OutputTargetArgs {
        save: &args.save,
        chunks: &args.chunks,
        stdout: args.stdout,
        format_output: &args.format_output,
    };

    // Use trigger_generation which handles the core logic + output
    trigger_generation(&project_root, &config, &output_target_args, quiet, verbose) // Pass correct type
}

// This function now encapsulates the core generation logic
// It's called by both `generate` and `watch` commands
// Made public so watch.rs can use it
pub fn trigger_generation(
    project_root: &Path,
    config: &Arc<Config>,
    output_target_args: &OutputTargetArgs, // Now expects this type
    quiet: bool,
    verbose: u8,
) -> Result<()> {
    log::info!(
        "Starting context generation for: {}",
        project_root.display()
    );

    validate_args_for_generation(config, output_target_args)?;

    log::debug!("Gathering files and tree elements...");
    let (source_files, docs_files, tree_path_types) =
        core::gather_files_and_tree(project_root, config, quiet)
            .context("Failed to gather project files and directory structure")?;
    log::debug!(
        "Gathering complete. Found {} source, {} docs, {} tree elements.",
        source_files.len(),
        docs_files.len(),
        tree_path_types.len()
    );

    let tree_for_context: Option<Vec<core::TreeNode>> = if config.tree.enabled {
        log::debug!("Building tree structure...");
        let tree = core::gather::build_tree_from_paths(&tree_path_types)
            .context("Failed to build directory tree structure")?;
        log::debug!("Tree structure built.");
        Some(tree)
    } else {
        log::debug!("Tree structure disabled in config.");
        None
    };

    log::debug!("Detecting project characteristics...");
    let project_characteristics = core::detect_project_characteristics(project_root)
        .context("Failed to detect project characteristics")?;
    log::debug!("Characteristics detected: {:?}", project_characteristics);

    log::debug!("Building initial project context (including rule resolution)...");
    let mut main_context = ProjectContext::build(
        project_root,
        config,
        tree_for_context,
        &project_characteristics,
    )
    .context("Failed to build initial project context")?;
    log::debug!("Initial context built.");

    // Add docs if enabled
    main_context = main_context.add_docs(docs_files, project_root, config);

    // Handle source files (inline or chunking)
    if config.source.enabled {
        log::debug!("Processing source files...");
        if let Some(chunk_size_str) = output_target_args.chunks.as_deref() {
            log::info!("Chunking source files with size: {}", chunk_size_str);

            let (save_dir, filename_base, _) =
                get_save_details_from_args(config, output_target_args.save.as_ref(), project_root);

            let chunk_files_data =
                core::chunking::split_files_into_chunks(source_files, chunk_size_str, project_root)
                    .context("Failed to split files into chunks")?;

            let mut chunk_file_paths = Vec::<PathBuf>::new();
            if !chunk_files_data.is_empty() {
                fs::create_dir_all(&save_dir).with_context(|| {
                    // Added std::fs import
                    format!(
                        "Failed to create chunk output directory {}",
                        save_dir.display()
                    )
                })?;
            }

            for (i, chunk_data) in chunk_files_data.iter().enumerate() {
                let chunk_num = i + 1;
                let chunk_filename = format!("{}_chunk_{}.json", filename_base, chunk_num); // Chunks always JSON
                let chunk_path = save_dir.join(&chunk_filename);
                // Use output::save_chunk_file, passing format_opts from output_target_args
                output::save_chunk_file(
                    chunk_data,
                    &chunk_path,
                    &output_target_args.format_output,
                    quiet,
                )?;
                chunk_file_paths.push(chunk_path);
            }

            main_context = main_context.add_chunk_paths(chunk_file_paths, &save_dir, config);
            log::info!("Chunking processing complete.");

            // Output the main context file (without sources, just chunk refs) if saving is requested
            if output_target_args.save.is_some() {
                let (main_save_dir, main_filename_base, main_extension) =
                    get_save_details_from_args(
                        config,
                        output_target_args.save.as_ref(),
                        project_root,
                    );
                let main_filename = format!("{}.{}", main_filename_base, main_extension);
                let main_output_path = main_save_dir.join(main_filename);
                log::info!(
                    "Saving main context (with chunk references) to file: {}",
                    main_output_path.display()
                );
                output::print_context_or_save(
                    &main_context,
                    config,
                    Some(&main_output_path),
                    &output_target_args.format_output,
                    quiet,
                )?;
            } else if output_target_args.stdout {
                // If stdout is forced even with chunking, print the main context (with chunk refs)
                log::info!("Outputting main context (with chunk references) to stdout...");
                output::print_context_or_save(
                    &main_context,
                    config,
                    None,
                    &output_target_args.format_output,
                    quiet,
                )?;
            } else if !quiet {
                // Print confirmation about chunks being saved if not outputting main context elsewhere
                println!(
                    "{} Source content chunked and saved in: {}",
                    "âœ…".green(),                          // Added Colorize import
                    save_dir.display().to_string().blue()  // Added Colorize import
                );
            }
        } else {
            log::debug!("Adding source files inline...");
            main_context = main_context.add_files(source_files, project_root, config);
            // Output main context (with inline sources)
            handle_final_output(
                &main_context,
                config,
                output_target_args,
                project_root,
                quiet,
            )?;
        }
    } else {
        log::debug!("Source section disabled.");
        if !source_files.is_empty() && !quiet && verbose > 0 {
            eprintln!(
                "{}",
                "Warning: Source section disabled, but source files were found and ignored."
                    .yellow() // Added Colorize import
            );
        }
        // Output main context (without any source section)
        handle_final_output(
            &main_context,
            config,
            output_target_args,
            project_root,
            quiet,
        )?;
    }

    Ok(())
}

// Define a helper struct to pass output-related args cleanly
// Made public so watch.rs can use it
pub struct OutputTargetArgs<'a> {
    pub save: &'a Option<Option<PathBuf>>,
    pub chunks: &'a Option<String>,
    pub stdout: bool,
    pub format_output: &'a crate::cli_args::FormatOutputOpts,
}

// Helper to get save details from OutputTargetArgs
fn get_save_details_from_args(
    config: &Config,
    cli_save_opt: Option<&Option<PathBuf>>,
    project_root: &Path,
) -> (PathBuf, String, String) {
    let save_dir_base = match cli_save_opt {
        Some(Some(cli_path)) => {
            log::trace!(
                "Save directory explicitly provided via CLI: {}",
                cli_path.display()
            );
            cli_path.clone()
        }
        Some(None) => {
            log::trace!(
                "Save flag used without path, using configured/default save directory: {}",
                config.save.output_dir.display()
            );
            config.save.output_dir.clone()
        }
        None => {
            log::trace!(
                "Save flag not used, using configured/default save directory for potential chunks: {}",
                config.save.output_dir.display()
            );
            config.save.output_dir.clone() // Default needed if chunking without -s
        }
    };

    let save_dir = if save_dir_base.is_absolute() {
        save_dir_base
    } else {
        project_root.join(save_dir_base)
    };
    log::trace!("Resolved absolute save directory: {}", save_dir.display());

    let name_options: [Option<&str>; 3] = [
        config.save.filename_base.as_deref(),
        config.general.project_name.as_deref(),
        project_root.file_name().and_then(|n| n.to_str()),
    ];
    let filename_base_str = name_options
        .into_iter()
        .flatten()
        .next()
        .unwrap_or("context"); // Fallback base name
    let filename_base = filename_base_str.to_string();
    log::trace!("Using filename base: {}", filename_base);

    let extension = config.save.extension.as_deref().unwrap_or_else(|| {
        match config.output.format.to_lowercase().as_str() {
            "yaml" | "yml" => "yaml",
            "xml" => "xml",
            _ => "json",
        }
    });
    log::trace!("Using save extension: {}", extension);

    (save_dir, filename_base, extension.to_string())
}

fn handle_final_output(
    main_context: &ProjectContext,
    config: &Config,
    output_target_args: &OutputTargetArgs,
    project_root: &Path,
    quiet: bool,
) -> Result<()> {
    log::debug!("Determining final output target...");
    let mut output_target_path: Option<PathBuf> = None;
    let needs_saving_to_disk = output_target_args.save.is_some();

    if needs_saving_to_disk {
        let (save_dir, filename_base, extension) =
            get_save_details_from_args(config, output_target_args.save.as_ref(), project_root);
        let main_filename = format!("{}.{}", filename_base, extension);
        output_target_path = Some(save_dir.join(main_filename));
        log::debug!(
            "Output target path set to file: {}",
            output_target_path.as_ref().unwrap().display()
        );
    } else if output_target_args.stdout {
        log::debug!("Output target set to stdout (forced).");
    } else {
        log::debug!("Output target set to stdout (default).");
    }

    output::print_context_or_save(
        main_context,
        config,
        output_target_path.as_deref(),
        &output_target_args.format_output,
        quiet,
    )
}

fn validate_args_for_generation(config: &Config, args: &OutputTargetArgs) -> Result<()> {
    if args.chunks.is_some() {
        if !config.source.enabled {
            anyhow::bail!(core::AppError::InvalidArgument(
                "Chunking (-c) cannot be used when source file inclusion ([source].enabled=false) is disabled".to_string()
            ));
        }
        let format = args
            .format_output
            .format
            .as_deref()
            .unwrap_or(&config.output.format);
        if format.to_lowercase() != "json" {
            anyhow::bail!(core::AppError::Chunking(
                "Chunking requires the output format to be 'json'. Use '-f json'.".to_string()
            ));
        }
        // Chunking implies saving, so stdout without save doesn't make sense unless explicitly handled
        if args.stdout && args.save.is_none() {
            anyhow::bail!(core::AppError::InvalidArgument(
                 "--stdout cannot be used with --chunks unless --save is also specified to define the main context output location.".to_string()
             ));
        }
    }
    Ok(())
}

use crate::cli_args::MetricsArgs;
use crate::load_config_for_command;
use crate::output::{print_data_or_text, print_metrics_pretty_table};
use anyhow::{Context, Result};
use byte_unit::{Byte, UnitType};
use log;
use pathdiff; // Added use
use serde::Serialize;
use std::path::Path;
use tiktoken_rs::cl100k_base;
use xcontext_core::{self as core, Config, FileInfo}; // Use core types

#[derive(Debug, Serialize)]
pub struct ProjectMetrics {
    pub total_files: usize,
    pub total_lines: usize,
    pub total_bytes: u128,
    pub total_bytes_readable: String,
    pub estimated_tokens: usize,
    pub files_details: Vec<FileMetrics>,
}

#[derive(Debug, Serialize)]
pub struct FileMetrics {
    pub path: String,
    pub lines: usize,
    pub bytes: usize,
    pub bytes_readable: String,
    pub estimated_tokens: usize,
}

pub fn handle_metrics_command(args: MetricsArgs, quiet: bool) -> Result<()> {
    let project_root = Config::determine_project_root(args.project_config.project_root.as_ref())
        .context("Failed to determine project root")?;
    log::info!("Project root determined: {}", project_root.display());

    let config = load_config_for_command(
        &project_root,
        &args.project_config,
        None,
        None,
        Some(&args.format_output), // Pass format override options
    )
    .context("Failed to load configuration for metrics command")?;

    log::debug!("Gathering files for metrics...");
    let (source_files, docs_files, _) = core::gather_files_and_tree(&project_root, &config, quiet)
        .context("Failed to gather files for metrics calculation")?;
    log::debug!("Files gathered.");

    let combined_files: Vec<&FileInfo> = source_files.iter().chain(docs_files.iter()).collect();

    if combined_files.is_empty() && !quiet {
        println!("No source or documentation files found to calculate metrics.");
        return Ok(()); // Exit gracefully if no files
    }

    log::debug!("Calculating metrics...");
    let metrics = calculate_metrics(&combined_files, &project_root)?;
    log::debug!("Metrics calculation complete.");

    if args.format_output.format.is_none() {
        print_metrics_pretty_table(&metrics)
    } else {
        // Pass None for plain_text, rely on structured output
        print_data_or_text(
            &metrics,
            None,
            &args.format_output,
            "json",
            "ProjectMetrics",
        )
    }
}

fn calculate_metrics(files: &[&FileInfo], project_root: &Path) -> Result<ProjectMetrics> {
    let bpe =
        cl100k_base().map_err(|e| anyhow::anyhow!(core::AppError::TikToken(e.to_string())))?;
    let mut total_files = 0;
    let mut total_lines = 0;
    let mut total_bytes: u128 = 0;
    let mut total_tokens = 0;
    let mut files_details = Vec::new();

    for file_info in files {
        if file_info.size == 0 {
            continue;
        } // Skip empty files

        let lines = file_info.content.lines().count();
        let bytes = file_info.size;
        // Estimate tokens in parallel? Might be overkill unless content is huge
        let tokens = bpe.encode_ordinary(&file_info.content).len();

        let relative_path = pathdiff::diff_paths(&file_info.path, project_root) // Added use pathdiff
            .unwrap_or_else(|| file_info.path.clone())
            .to_string_lossy()
            .to_string();

        total_files += 1;
        total_lines += lines;
        total_bytes = total_bytes.saturating_add(bytes as u128);
        total_tokens += tokens;

        let file_byte = Byte::from_u128(bytes as u128).unwrap_or_default();
        let file_size_readable = file_byte.get_appropriate_unit(UnitType::Binary).to_string();

        files_details.push(FileMetrics {
            path: relative_path,
            lines,
            bytes,
            bytes_readable: file_size_readable,
            estimated_tokens: tokens,
        });
    }

    // Sort by path for consistent output
    files_details.sort_by(|a, b| a.path.cmp(&b.path));

    let total_byte = Byte::from_u128(total_bytes).unwrap_or_default();
    let total_size_readable = total_byte
        .get_appropriate_unit(UnitType::Binary)
        .to_string();

    Ok(ProjectMetrics {
        total_files,
        total_lines,
        total_bytes,
        total_bytes_readable: total_size_readable,
        estimated_tokens: total_tokens,
        files_details,
    })
}

use crate::cli_args::QuickArgs; // Removed unused FormatOutputOpts
use crate::load_config_for_command;
use crate::output::print_data_or_text;
use anyhow::{Context, Result};
use colored::*;
use glob::Pattern;
use ignore::{WalkBuilder, WalkState};
use log;
use rayon::iter::{IntoParallelRefIterator, ParallelIterator};
// Removed: use rayon::prelude::*;
use serde::Serialize;
use std::collections::HashMap;
use std::fs;
use std::path::PathBuf; // Removed unused Path import
use std::sync::mpsc;
use xcontext_core::Config;

#[derive(Debug, Serialize)]
struct QuickOutput {
    files: HashMap<String, String>,
}

pub fn handle_quick_command(args: QuickArgs, quiet: bool, verbose: u8) -> Result<()> {
    let project_root = Config::determine_project_root(args.project_config.project_root.as_ref())
        .context("Failed to determine project root")?;
    log::info!("Project root determined: {}", project_root.display());

    // Load config primarily to respect ignore rules (.gitignore, built-in)
    let config = load_config_for_command(
        &project_root,
        &args.project_config,
        None,
        None,
        Some(&args.format_output), // Pass format override options
    )
    .context("Failed to load configuration for quick command")?;

    let mut pattern_to_use = args.pattern.clone();
    let potential_path = project_root.join(&args.pattern);
    let mut info_msg = None;

    // Check if the pattern looks like a directory and adjust glob
    if potential_path.is_dir() {
        pattern_to_use = format!(
            "{}**/*",
            args.pattern.trim_end_matches(&['/', '\\'] as &[char])
        );
        info_msg = Some(format!(
            "{} Interpreting directory input '{}' as glob '{}'",
            "â„¹ï¸".blue(),
            args.pattern,
            pattern_to_use
        ));
    } else if args.pattern.ends_with(&['/', '\\'] as &[char]) {
        // If it ends with slash but isn't a dir, warn and use modified pattern
        if !quiet {
            eprintln!(
                "{} Directory pattern '{}' matches no existing directory, using pattern without trailing slash.",
                "âš ï¸".yellow(),
                args.pattern
            );
        }
        pattern_to_use = args
            .pattern
            .trim_end_matches(&['/', '\\'] as &[char])
            .to_string();
    }

    if let Some(msg) = info_msg {
        if !quiet && verbose > 0 {
            eprintln!("{}", msg);
        }
    }

    let glob_pattern = Pattern::new(&pattern_to_use).with_context(|| {
        format!(
            "Invalid glob pattern for quick: '{}' (processed as '{}')",
            args.pattern, pattern_to_use
        )
    })?;

    let use_gitignore = config.general.use_gitignore;
    let _enable_builtin_ignore = config.general.enable_builtin_ignore; // TODO: Apply built-in ignores too?

    let mut builder = WalkBuilder::new(&project_root);
    builder.hidden(false); // Include hidden files unless filtered by ignores
    builder.ignore(use_gitignore);
    builder.git_ignore(use_gitignore);
    builder.git_exclude(use_gitignore);
    builder.require_git(false);
    // TODO: Add logic to apply built-in ignores here if desired for `quick`

    let walker = builder.build_parallel();
    let (tx_path, rx_path) = mpsc::channel::<PathBuf>();
    let glob_pattern_outer_clone = glob_pattern.clone(); // Clone for closure
    let proj_root_clone = project_root.clone(); // Clone for closure

    log::debug!(
        "Starting parallel walk for pattern: {}",
        glob_pattern.as_str()
    );
    walker.run(move || {
        // tx_path is MOVED here
        let tx = tx_path.clone(); // Clone the moved sender for the inner closure
        let proj_root_inner = proj_root_clone.clone();
        let glob_pattern_inner_clone = glob_pattern_outer_clone.clone();

        Box::new(move |entry_result| {
            if let Ok(entry) = entry_result {
                if entry.file_type().map_or(false, |ft| ft.is_file()) {
                    if let Some(relative_path) =
                        pathdiff::diff_paths(entry.path(), &proj_root_inner)
                    {
                        if glob_pattern_inner_clone.matches_path(&relative_path) {
                            log::trace!("Matched file: {}", relative_path.display());
                            // Send using the cloned sender for this thread
                            let _ = tx.send(entry.path().to_path_buf());
                        }
                    } else if glob_pattern_inner_clone.matches_path(entry.path()) {
                        log::trace!("Matched absolute path: {}", entry.path().display());
                        let _ = tx.send(entry.path().to_path_buf());
                    }
                }
            }
            WalkState::Continue
        })
    }); // The original tx_path (owned by the closure) goes out of scope here

    // Removed: drop(tx_path); // No longer needed, and tx_path was moved

    let paths_to_read: Vec<_> = rx_path.into_iter().collect(); // This will finish when all senders (clones) are dropped
    log::info!(
        "Found {} files matching pattern. Reading content...",
        paths_to_read.len()
    );

    let results: Vec<Result<(String, String)>> = paths_to_read
        .par_iter()
        .map(|path| {
            let content = fs::read_to_string(path)
                .with_context(|| format!("Failed to read file {}", path.display()))?;
            let relative_path = pathdiff::diff_paths(path, &project_root)
                .unwrap_or_else(|| path.clone())
                .to_string_lossy()
                .to_string();
            Ok((relative_path, content))
        })
        .collect();

    let mut files_map = HashMap::new();
    let mut read_errors = Vec::new();

    for result in results {
        match result {
            Ok((path_str, content)) => {
                files_map.insert(path_str, content);
            }
            Err(e) => {
                read_errors.push(format!("{:#}", e));
            }
        }
    }

    if !read_errors.is_empty() && !quiet {
        eprintln!(
            "{}",
            "Warning: Errors encountered during file reading:".yellow()
        );
        for err_msg in read_errors {
            eprintln!(" - {}", err_msg);
        }
        eprintln!("---");
    }

    if files_map.is_empty() && !quiet {
        println!("No files matched the pattern '{}'.", args.pattern);
        return Ok(());
    }

    let output_data = QuickOutput { files: files_map };

    let default_format = "json";
    print_data_or_text(
        &output_data,
        None,
        &args.format_output,
        default_format,
        "QuickOutput",
    )
}

use crate::cli_args::{FormatOutputOpts, ShowArgs}; // Removed unused Cli, ProjectConfigOpts, ShowItem
use crate::load_config_for_command;
use crate::output::print_data_or_text; // Use CLI output helpers
use anyhow::{Context, Result};
use colored::*;
use log; // Corrected: On its own line
use serde::Serialize; // Needed for ShowOutputWrapper
use std::collections::{BTreeMap, HashMap};
